const {decodeBits, decodeMorse} = require('../../CodeWars/MorseCodeAdv')

describe.skip('MorseCode Advanced', () => {
    
    it('Binary to Morse to String', () => {
        expect(decodeMorse(decodeBits('1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011')))
        .toBe('HEY JUDE')
    })

    it('Binary to Morse', () => {
        expect(decodeBits('1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011'))
        .toBe('.... . -.--   .--- ..- -.. .')
    }) 

    it('Expect EE', () => {
        expect(decodeMorse(decodeBits('10001'))).toBe("EE")
    })

    it('Expect A', () => {
        expect(decodeMorse(decodeBits('10111'))).toBe("A")
    })

    it('Expect E', () => {
        expect(decodeMorse(decodeBits('111'))).toBe("E")
    })
    it('Expect E', () => {
        expect(decodeMorse(decodeBits('1111111'))).toBe("E")
    })
    it('Expect M', () => {
        expect(decodeMorse(decodeBits('1110111'))).toBe("M")
    })
    it('Expect I', () => {
        expect(decodeMorse(decodeBits('111110000011111'))).toBe("I")
    })
    it('Expect M', () => {
        expect(decodeMorse(decodeBits('11111100111111'))).toBe("M")
    })


    it('bloody essay', () => {
        expect(decodeMorse(decodeBits('')))
        .toBe("THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG.")
    })
})